function displayProducts(productsToShow = products) {
  const productsGrid = document.getElementById('products-grid');
  if (!productsGrid) return;

  if (productsToShow.length === 0) {
    productsGrid.innerHTML = `<p class="text-center">No products found for this category.</p>`;
    return;
  }

  productsGrid.innerHTML = productsToShow.map(product => `
    <div class="col-md-6 col-lg-4 mb-4">
      <div class="card h-300 shadow-sm">
        <div class="condition-tag ${product.condition?.toLowerCase() || 'good'}">${product.condition || 'Good'}</div>
        <div class="discount-badge">${product.discount}% OFF</div>
        <img src="${product.image}" class="card-img-top product-image" alt="${product.name}">
        <div class="card-body">
          <h5 class="card-title">${product.name}</h5>
          <div class="price-info">
            <p class="card-text text-danger fw-bold mb-0">PKR ${product.price}</p>
            <p class="card-text text-muted text-decoration-line-through mb-0">PKR ${product.originalPrice}</p>
          </div>
          <p class="card-text text-muted mb-2">${product.brand.toUpperCase()}</p>
          <div class="sizes mb-3">
            <small class="text-muted">Available Sizes:</small>
            <div class="d-flex gap-2 mt-1">
              ${product.size.map(size => `
                <span class="size-badge">${size}</span>
              `).join('')}
            </div>
          </div>
        </div>
      </div>
    </div>
  `).join('');
}

function filterProductsByCategory(category) {
  const filteredProducts = products.filter(product => product.category === category);
  displayProducts(filteredProducts);
}

function setupFilters() {
  const brandFilter = document.getElementById('brand-filter');
  const categoryFilter = document.getElementById('category-filter');
  const priceFilter = document.getElementById('price-filter');

  [brandFilter, categoryFilter, priceFilter].forEach(filter => {
    if (filter) {
      filter.addEventListener('change', filterProducts);
    }
  });
}

function filterProducts() {
  const brand = document.getElementById('brand-filter')?.value || '';
  const category = document.getElementById('category-filter')?.value || '';
  const priceRange = document.getElementById('price-filter')?.value || '';

  let filtered = [...products];

  if (brand) {
    filtered = filtered.filter(p => p.brand === brand);
  }

  if (category) {
    filtered = filtered.filter(p => p.category === category);
  }

  if (priceRange) {
    const [min, max] = priceRange.split('-').map(n => parseInt(n) || Infinity);
    filtered = filtered.filter(p => p.price >= min && (max === Infinity ? true : p.price <= max));
  }

  displayProducts(filtered);
}
